mapper.select:
select * from file

    file_type=#{fileType}
    and file_name like concat('%',#{fileName},'%')
    and user_id=#{userId} and space_id=#{spaceId}

mapper.update:
update file
file_name=#{newFileName}, file_type=#{fileType}, file_path=#{filePath},
where user_id=#{userId} and file_id=#{fileId}
