mapper.select:
select * from file

    file_type=#{fileType}
    and file_name like concat('%',#{fileName},'%')
    and space_id=#{spaceId}
    and is_delete=#{isDelete}

mapper.update:
update file
file_name=#{fileName}, file_path=#{filePath}, update_time=#{updateTime}, space_id=#{spaceId},
where file_id=#{fileId}
